import { Component, OnDestroy, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Subscription } from 'rxjs';
import { LoadingService } from './loading.service';

@Component({
  selector: 'app-loading',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './loading.component.html',
  styleUrls: ['./loading.component.scss']
})
export class LoadingComponent implements OnInit, OnDestroy {

  loading = false;
  loadingSuscription!: Subscription;

  constructor(private loadingService: LoadingService) { }

  ngOnInit() {
    this.loadingSuscription = this.loadingService.loadingStatus
      .subscribe((value: boolean) => {
        this.loading = value;
      });
  }

  ngOnDestroy() {
    this.loadingSuscription.unsubscribe();
  }

}

<div class="loading-screen-wrapper" *ngIf="loading">
  <div class="loading-screen-icon">
    <div class="d-flex justify-content-between align-items-center"
         style="padding:15px;border:5px solid #333;box-shadow:inset;border-radius:5px;">
      <span class="loader"></span>
      <br />
      <b>Cargando</b>
    </div>
  </div>
</div>
