import { Component } from '@angular/core';
import { ArticleDTO } from '../../models/article';
import { ArticleService } from '../../services/article.service';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-articles',
  templateUrl: './articles.component.html',
  styleUrls: ['./articles.component.scss']
})
export class ArticlesComponent {

  articles: Array<ArticleDTO> = [];
  disabledAddButtons: boolean;

  constructor(private articleService: ArticleService,
    private authService: AuthService) {
    const token: string | undefined = authService.sessionTokenValue();
this.disabledAddButtons = token === undefined;

    articleService.Articles$
      .subscribe(res => this.articles = res);
  }

  addToCart(article: ArticleDTO) {
  }

}

<h2 class="mt-3 mb-3">
  Tienda
</h2>

<div class="row">
  <div class="col-md-4 col-sm-12"
       *ngFor="let article of articles">
    <div class="card mt-5 mb-5 p-3">
      <img class="card-img-top article-image"
           [src]="article.image"
           [title]="article.description"
           [alt]="article.description" />
      <div class="card-body">
        <h3 class="card-title text-center mt-2 mb-2">
          {{ article.description }}
        </h3>
        <div class="d-flex justify-content-between align-items-center">
          <p>{{ article.price | currency }}</p>
          <p>{{ article.stock }} unidades</p>
        </div>
        <button class="btn btn-outline-primary"
                style="width: 100%;"
                [disabled]="disabledAddButtons"
                (click)="addToCart(article)">
          Agregar al Carrito
        </button>
      </div>
    </div>

  </div>
</div>
