import { Component } from '@angular/core';
import { ArticleDTO } from '../../models/article';
import { FormGroup } from '@angular/forms';

@Component({
  selector: 'app-articles',
  templateUrl: './articles.component.html',
  styleUrls: ['./articles.component.scss']
})
export class ArticlesComponent {

  articles: Array<ArticleDTO> = [];

  articleForm!: FormGroup;
  showForm: boolean = false;
  isEdition: boolean = false;

  article!: StoreDTO;

  constructor(private storeService: StoreService,
    private fb: FormBuilder) {
    storeService.Stores$
      .subscribe(res => this.articles = res);
  }

  initForm() {
    this.articleForm = this.fb.group({
      branch: ['', [Validators.required]],
      address: ['', [Validators.required]],
    });
  }

  setFormVisibility = () => this.showForm = !this.showForm;

  addNew() {
    this.isEdition = false;
    this.setFormVisibility();
    this.initForm();
  }

  isValidForm = (): boolean => this.articleForm.valid;

  save() {
    if (this.isValidForm()) {
      this.storeService.insert(this.articleForm.value)
        .subscribe(res => {
          if (Response) {
            this.setFormVisibility();
          }
        });
    }
  }

  edit(store: StoreDTO) {
    this.article = store;
    this.isEdition = true;
    this.setFormVisibility();
    this.initForm();
    this.articleForm.patchValue(store);
  }

  update() {
    if (this.isValidForm()) {
      this.storeService.update(this.article.id, this.articleForm.value)
        .subscribe(response => {
          if (Response) {
            this.setFormVisibility();
          }
        });
    }
  }

  delete(store: StoreDTO) {
    if (confirm(`¿Desea eliminar la tienda ${store.branch}?`)) {
      this.storeService.delete(store.id)
        .subscribe(response => {
          if (Response) {
            alert("Registro eliminado");
          }
        });
    }
  }

}
