import { Component } from '@angular/core';
import { ShoppingCartItemDTO } from '../../models/article';
import { ShoppingCartService } from '../../services/shopping-cart.service';

@Component({
  selector: 'app-shopping-cart',
  templateUrl: './shopping-cart.component.html',
  styleUrls: ['./shopping-cart.component.scss']
})
export class ShoppingCartComponent {

  cartItems: Array<ShoppingCartItemDTO> = [];

  constructor(private shoppingCartService: ShoppingCartService) {
    shoppingCartService.CartItems$
      .subscribe(items => {
this.cartItems = items;
      });
  }

  calculateTotal(): number {
    if (this.cartItems && this.cartItems.length > 0) {
return this.cartItems
        .map(item => item.price)
        .reduce((a, b) => a + b);
    } else {
      return 0;
    }
  }

}

<h2 class="mt-3 mb-3">
  Tu carrito
</h2>

<div class="row">
  <div class="col-md-8 col-sm-12">
    <div class="list-group">
      <div class="list-group-item d-flex justify-content-between align-items-center"
           *ngFor="let item of cartItems">
        <img [src]="item.image" [title]="item.description"
             class="shopping-cart-item-image" />
        <h5>{{ item.description }}</h5>
        <span class="badge">
          {{ item.price | currency }}
        </span>
      </div>
    </div>

  </div>
  <div class="col-md-4 col-sm-12">
    <h3>Resúmen</h3>
    <div class="list-group">
      <div class="list-group-item d-flex justify-content-between align-items-center">
        <b>Total de productos</b>
        <span class="badge">{{ cartItems.length }}</span>
      </div>
      <div class="list-group-item d-flex justify-content-between align-items-center">
        <b>TOTAL</b>
        <span class="badge">{{ calculateTotal() | currency }}</span>
      </div>
    </div>
  </div>
</div>
