import { Component } from '@angular/core';
import { ArticleDTO } from '../../models/article';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ArticleService } from '../../services/article.service';

@Component({
  selector: 'app-articles',
  templateUrl: './articles.component.html',
  styleUrls: ['./articles.component.scss']
})
export class ArticlesComponent {

  articles: Array<ArticleDTO> = [];

  articleForm!: FormGroup;
  showForm: boolean = false;
  isEdition: boolean = false;

  article!: ArticleDTO;

  constructor(private articleService: ArticleService,
    private fb: FormBuilder) {
    articleService.Articles$
      .subscribe(res => this.articles = res);
  }

  initForm() {
    this.articleForm = this.fb.group({
      code: ['', [Validators.required]],
      description: ['', [Validators.required]],
      price: ['', [Validators.required]],
      stock: ['', [Validators.required]],
      image: ['', [Validators.required]],
    });
  }

  setFormVisibility = () => this.showForm = !this.showForm;

  addNew() {
    this.isEdition = false;
    this.setFormVisibility();
    this.initForm();
  }

  isValidForm = (): boolean => this.articleForm.valid;

  save() {
    if (this.isValidForm()) {
      this.articleService.insert(this.articleForm.value)
        .subscribe(response => {
          if (Response) {
            this.setFormVisibility();
          }
        });
    }
  }

  edit(article: ArticleDTO) {
    this.article = article;
    this.isEdition = true;
    this.setFormVisibility();
    this.initForm();
    this.articleForm.patchValue(article);
  }

  update() {
    if (this.isValidForm()) {
      this.articleService.update(this.article.id, this.articleForm.value)
        .subscribe(response => {
          if (Response) {
            this.setFormVisibility();
          }
        });
    }
  }

  delete(article: ArticleDTO) {
    if (confirm(`¿Desea eliminar el artículo ${article.description}?`)) {
      this.articleService.delete(article.id)
        .subscribe(response => {
          if (Response) {
            alert("Registro eliminado");
          }
        });
    }
  }

}

<div [hidden]="showForm">
  <div class="d-flex justify-content-between align-items-center mt-3 mb-3 pt-3 pb-3"
       style="border-bottom:2px solid ~ccc">
    <h2>
      Artículos
    </h2>
    <button class="btn btn-outline-secondaryt text-end"
            (click)="addNew()">
      Agregar
    </button>
  </div>
  <div class="table-responsive">
    <table class="table table-striped">
      <thead>
        <tr>
          <td></td>
          <td>Código</td>
          <td>Descripción</td>
          <td>Precio</td>
          <td>Existencia</td>
          <td></td>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let article of articles">
          <td>
            <img [src]="article.image" [title]="article.description"
                 class="article-image" />
          </td>
          <td>{{ article.code }}</td>
          <td>{{ article.description }}</td>
          <td>{{ article.price | currency }}</td>
          <td>{{ article.stock | number }}</td>
          <td class="text-end">
            <div class="btn-group text-end">
              <button class="btn btn-secondary btn-sm"
                      (click)="edit(article)">
                Editar
              </button>
              <button class="btn btn-secondary btn-sm"
                      (click)="delete(article)">
                Eliminar
              </button>
            </div>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>

<ng-container *ngIf="showForm">
  <div class="form-group">
    <h3 class="mt-5 mb-5">
      {{ !isEdition ? 'Agregar nuevo artículo' : 'Editar artículo' }}
    </h3>
    <form [formGroup]="articleForm">
      <div class="mb-3">
        <label for="code" class="form-label">Código:</label>
        <input type="text" class="form-control" id="code" formControlName="code" required autofocus />
      </div>

      <div class="mb-3">
        <label for="description" class="form-label">Descripción:</label>
        <input type="text" class="form-control" id="description" formControlName="description" required>
      </div>
      <div class="mb-3">
        <label for="price" class="form-label">Precio:</label>
        <input type="number" class="form-control" id="price" formControlName="price" required>
      </div>
      <div class="mb-3">
        <label for="stock" class="form-label">Existencia:</label>
        <input type="number" class="form-control" id="stock" formControlName="stock" required>
      </div>
      <div class="mb-3">
        <label for="image" class="form-label">Url de imagen:</label>
        <input type="text" class="form-control" id="image" formControlName="image" required>
      </div>

      <div class="d-flex justify-content-around align-items-center" mt-3 mb-2>
        <button type="submit" *ngIf="!isEdition"
                class="btn btn-primary"
                [disabled]="!isValidForm()"
                (click)="save()">
          Guardar
        </button>
        <button type="submit" *ngIf="isEdition"
                class="btn btn-primary"
                [disabled]="!isValidForm()"
                (click)="update()">
          Guardar cambios
        </button>
        <button type="submit"
                class="btn btn-secondary"
                (click)="setFormVisibility()">
          Cancelar
        </button>
      </div>
    </form>
  </div>
</ng-container>
