import { Component } from '@angular/core';
import { StoreDTO } from '../../models/Store';
import { StoreService } from '../../services/store.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-stores',
  templateUrl: './stores.component.html',
  styleUrls: ['./stores.component.scss']
})
export class StoresComponent {

  stores: Array<StoreDTO> = [];

  storeForm!: FormGroup;
  showForm: boolean = false;

  constructor(private storeService: StoreService,
  private fb:FormBuilder) {
    storeService.Stores$
      .subscribe(res => this.stores=res);
  }

  initForm() {
    this.storeForm = this.fb.group({
      branch: ['', [Validators.required]],
      address: ['', [Validators.required]],
    });
  }

  setFormVisibility = () => this.showForm = !this.showForm;

  addNew() {
    this.setFormVisibility();
this.initForm();
  }

  isValidForm = (): boolean => this.storeForm.valid;

save() {
    if (this.isValidForm()) {
      this.storeService.insert(this.storeForm.value)
        .subscribe(res => {
          if (Response) {
            this.setFormVisibility();
          }
        });
    }
  }

}

<div [hidden]="showForm">
  <div class="d-flex justify-content-between align-items-center mt-3 mb-3 pt-3 pb-3"
       style="border-bottom:2px solid ~ccc">
    <h2>
      Tiendas
    </h2>
    <button class="btn btn-outline-secondaryt text-end"
            (click)="addNew()">
      Agregar
    </button>
  </div>
  <div class="table-responsive">
    <table class="table table-striped">
      <thead>
        <tr>
          <td>Sucursal</td>
          <td>Domicilio</td>
          <td></td>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let store of stores">
          <td>{{ store.branch }}</td>
          <td>{{ store.address }}</td>
          <td>
            <button class="btn btn-secondary btn-sm">
              Editar
            </button>
            <button class="btn btn-secondary btn-sm">
              Eliminar
            </button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>

<ng-container *ngIf="showForm">
  <form [formGroup]="storeForm" class="form-group">
    <div class="mb-3">
      <label for="branch" class="form-label">Sucursal:</label>
      <input type="text" class="form-control" id="branch" formControlName="branch" required autofocus />
    </div>

    <div class="mb-3">
      <label for="address" class="form-label">Domicilio:</label>
      <input type="text" class="form-control" id="address" formControlName="address" required>
    </div>

    <button type="submit"
            class="btn btn-primary"
            [disabled]="!isValidForm()"
            (click)="save()">
      Guardar
    </button>
    <button type="submit"
            class="btn btn-secondary"
            (click)="setFormVisibility()">
      Cancelar
    </button>
  </form>
</ng-container>
