import { Component } from '@angular/core';
import { StoreDTO } from '../../models/Store';
import { StoreService } from '../../services/store.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-stores',
  templateUrl: './stores.component.html',
  styleUrls: ['./stores.component.scss']
})
export class StoresComponent {

  stores: Array<StoreDTO> = [];

  storeForm!: FormGroup;
  showForm: boolean = false;

  constructor(private storeService: StoreService,
  private fb:FormBuilder) {
    storeService.Stores$
      .subscribe(res => this.stores=res);
  }

  initForm() {
    this.storeForm = this.fb.group({
      branch: ['', [Validators.required]],
      address: ['', [Validators.required]],
    });
  }

  setFormVisibility = () => this.showForm = !this.showForm;

  addNew() {
    this.setFormVisibility();
this.initForm();
  }

  isValidForm = (): boolean => this.storeForm.valid;

save() {
    if (this.isValidForm()) {
      this.storeService.insert(this.storeForm.value)
        .subscribe(res => {
          if (Response) {
            this.setFormVisibility();
          }
        });
    }
  }

}

<div class="d-flex justify-content-between align-items-center mt-3 mb-3 pt-3 pb-3"
     style="border-bottom:2px solid ~ccc">
  <h2>
    Tiendas
  </h2>
  <button class="btn btn-outline-secondaryt text-end">
    Agregar
  </button>
</div>
<div class="table-responsive">
  <table class="table table-striped">
    <thead>
      <tr>
        <td>Sucursal</td>
        <td>Domicilio</td>
        <td></td>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let store of stores">
        <td>{{ store.branch }}</td>
        <td>{{ store.address }}</td>
        <td>
          <button class="btn btn-secondary btn-sm">
            Editar
          </button>
          <button class="btn btn-secondary btn-sm">
            Eliminar
          </button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
