import { Injectable } from '@angular/core';
import { Observable, ReplaySubject } from 'rxjs';
import { ArticleDTO } from '../models/article';
import { HttpClient } from '@angular/common/http';
import { ErrorService } from './error.service';
import { Result } from '../models/result';
import { environment } from '../../environment';

@Injectable({
  providedIn: 'root'
})
export class ShoppingCartService {

  apiUrl: string = `${environment.apiUrlBase}/Article`;;

  private _cartItems: ReplaySubject<Array<ArticleDTO>>;
  private list: Array<ArticleDTO> = [];

  constructor(private http: HttpClient,
    private errorService: ErrorService) {
    this._cartItems = new ReplaySubject<Array<ArticleDTO>>(1);
    this.getAll();
  }

  set CartItems(value: Array<ArticleDTO>) {
    this.list = value;
    this._cartItems.next(this.list);
    console.log(`Loaded ${value.length} cart items`);
  }

  get CartItems$(): Observable<Array<ArticleDTO>> {
    return this._cartItems.asObservable();
  }

  private httpCall(): Observable<Result<Array<ArticleDTO>>> {
    const uri = `${this.apiUrl}/GetShoppingCartItems`;
    return this.http
      .get<Result<Array<ArticleDTO>>>(uri);
  }

  private getAll() {
    this.httpCall().subscribe({
      next: (response) => {
        this.CartItems = response.data;
      }, error: (error) => {
        this.CartItems = [];
        this.errorService.logError(error);
      }
    })
  }
}
